import { Checkbox, IconButton } from '@mui/material';
import React, { useEffect, useState } from 'react';
import DeleteIcon from '@mui/icons-material/Delete';
import luggage1 from './luggage1.png';
import luggage2 from './luggage2.png';
import luggagebackground from './luggagebackground.png';

const TodoItem = (props) => {
    const {emitDeleteTodoItem} = props;
    const [todoItem, setTodoItem] = useState(props.data);
    const [isDirty, setDirty] = useState(false);
    useEffect(() => {
        if (isDirty){
            fetch(`http://localhost:8080/api/walizka/${todoItem.id}`,{
                method:"PUT",
                headers:{
                    "content-type":"application/json"
                },
                body: JSON.stringify(todoItem)
            })
            .then(response => response.json())
            .then(data => {
                setDirty(false);
                setTodoItem(data);
            });
        }
    },[todoItem, isDirty])

    function updateTask(e){
        setDirty(true);
        setTodoItem({...todoItem, task: e.target.value});
    }

    function deleteTodoItem(){
        fetch(`http://localhost:8080/api/walizka/${todoItem.id}`,{
                method:"DELETE",
                headers:{
                    "content-type":"application/json"
                },
            })
            .then(response => {
                emitDeleteTodoItem(todoItem);
            });

    }

    return (
        <div>
        <Checkbox
        checked={todoItem.isDone} 
        onChange={()=> {
            setDirty(true);
            setTodoItem({...todoItem, isDone: !todoItem.isDone})}}
        />
        {
            todoItem.isDone ? 
            <input type='text' className='done' value={todoItem.task} onChange={updateTask} style={{backgroundImage: `url(${luggagebackground})`, backgroundSize: '200% 100%', backgroundRepeat: 'no-repeat'}}/> : 
            <input type='text' value={todoItem.task} onChange={updateTask} style={{backgroundImage: `url(${luggagebackground})`, backgroundSize: '100% 100%', backgroundRepeat: 'no-repeat'}}/>
        }
        <IconButton aria-label="delete" size="large" onClick={deleteTodoItem}>
        <DeleteIcon fontSize="inherit" color="primary" />
        </IconButton>
      </div>
    );
};

export default TodoItem;
